import { Component, OnInit, Renderer2 } from '@angular/core';
import { FormBuilder, FormControl, FormGroup } from '@angular/forms';
import {ComponentsService} from '../components/components.service'

@Component({
    selector: 'app-components',
    templateUrl: './components.component.html',
    styles: [``]
})

export class ComponentsComponent implements OnInit {
    form: FormGroup;
    precios: any={};
    constructor( 
        private renderer : Renderer2,
        private readonly formBuilder: FormBuilder,
        private readonly componentsService:ComponentsService
        ) {
            this.form = this.formBuilder.group({
                Nombre: '',
                Apellido: '',
                Cedula: '',
                Email: '',
                Telefono: '',
                visitantes: null,
                reserva: ''
              });
        }

    ngOnInit() {     
        let input_group_focus = document.getElementsByClassName('form-control');
        let input_group = document.getElementsByClassName('input-group');
        for (let i = 0; i < input_group.length; i++) {
            input_group[i].children[0].addEventListener('focus', function (){
                input_group[i].classList.add('input-group-focus');
            });
            input_group[i].children[0].addEventListener('blur', function (){
                input_group[i].classList.remove('input-group-focus');
            });
        }
        this.listarPlanes()
    }

    guardarReserva(){             
        if (this.form.valid) {
            // Obtener los valores del formulario
            const nombre = this.form.get('Nombre')
            const apellido = this.form.get('Apellido')
            const cedula = this.form.get('Cedula')
            const email = this.form.get('Email')
            const telefono = this.form.get('Telefono')
            const visitantes = this.form.get('visitantes')
            const reserva = this.form.get('reserva')
      
            // Crear el objeto "body" con los datos
            const body = {
              nombre: nombre,
              apellido: apellido,
              cedula: cedula,
              email: email,
              telefono: telefono,
              numeroVisitantes: visitantes,
              fechaReserva: reserva
            };
        this.componentsService.gurdarReserva(body).subscribe((rest)=>{
            console.log(rest)
            alert("reserva exitosa")
        },(err)=>{
            console.log(err)
        })
     }
   }

   listarPlanes(){
    this.componentsService.listarPlanes().subscribe((rest)=>{
        console.log(rest.response);
        this.precios=rest.response;
    })
   }

}
