import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import {AuthService} from "../signin/service/auth.service"
import { Router } from '@angular/router';
@Component({
    selector: 'app-signin',
    templateUrl: './signin.component.html',
    styleUrls: ['./signin.component.scss']
})
export class SigninComponent implements OnInit {
    test : Date = new Date();
    form: FormGroup;
    constructor(
        private readonly formBuilder: FormBuilder,
        private service: AuthService,
        private router: Router
    ) { 
        this.form = this.formBuilder.group({
        email: '',
        password: '',
      })
     }

    ngOnInit() {

    }

    login() {
        console.log("entro")
        if (this.form.valid) {
            const email =this.form.get('email').value;
            const password =this.form.get('password').value  

            const loginRequest={
                email:email,
                password:password
            };
            this.service.login(loginRequest).subscribe((response) => {
          console.log(response);
          if (response.jwtToken) {            
            const jwtToken = response.jwtToken;
            localStorage.setItem('JWT', jwtToken);
            this.router.navigate(['/dash']);
          }
        },(err)=>{
          alert("usuario o clave incorrecta")
      })}

  }
}
<div class="wrapper">
    <div class="page-header" style="background-image: url('./assets/img/login-image.jpg');">
        <div class="filter"></div>
            <div class="container">
                <div class="row">
                    <div class="col-lg-4 col-sm-6 mr-auto ml-auto">
                      <div class="card card-register">
                          <h3 class="title">Welcome</h3>
                          <div class="social-line text-center">
                              <a href="#pablo" class="btn btn-neutral btn-facebook btn-just-icon">
                                  <i class="fa fa-facebook-square"></i>
                              </a>
                              <a href="#pablo" class="btn btn-neutral btn-google btn-just-icon">
                                  <i class="fa fa-google-plus"></i>
                              </a>
                              <a href="#pablo" class="btn btn-neutral btn-twitter btn-just-icon">
                                  <i class="fa fa-twitter"></i>
                              </a>
                          </div>

                          <form class="register-form" [formGroup]="form">
                              <label>Email</label>
                              <div class="input-group form-group-no-border" [ngClass]="{'input-group-focus':focus===true}">
                                <div class="input-group-prepend">
                                  <span class="input-group-text">
                                      <i class="nc-icon nc-email-85"></i>
                                  </span>
                                </div>

                                  <input type="text" class="form-control" placeholder="Email" (focus)="focus=true" (blur)="focus=false" formControlName="email" >
                              </div>

                              <label>Password</label>
                              <div class="input-group form-group-no-border" [ngClass]="{'input-group-focus':focus1===true}">
                                <div class="input-group-prepend">
                                  <span class="input-group-text">
                                      <i class="nc-icon nc-key-25"></i>
                                  </span>
                                </div>
                                  <input type="password" class="form-control" placeholder="Password" (focus)="focus1=true" (blur)="focus1=false" formControlName="password" >
                              </div>
                              <button class="btn btn-danger btn-block btn-round" (click)="login()">Ingresar</button>
                          </form>                          
                      </div>
                    </div>
                </div>
            </div>
    </div>
</div>
